/* global require */
(function () {
    "use strict";

    var fluid = require("infusion"),
        gpii = fluid.registerNamespace("gpii");

    fluid.registerNamespace("gpii.preferencesServer.post");

    fluid.defaults("kettle.requests.request.handler.preferencesPost", {
        gradeNames: ["autoInit", "fluid.littleComponent"],
        invokers: {
            handle: {
                funcName: "gpii.preferencesServer.post.request",
                args: [ "{preferencesServer}", "{request}", "{preferencesServer}.ontologyHandler", "{that}.events.onRawPreferences"],
                dynamic: true
            },
            postHandler: {
                funcName: "gpii.preferencesServer.post.postHandler",
                args: [ "{arguments}.0", "{request}", "{requestProxy}" ]
            }
        },
        events: {
            onRawPreferences: null
        },
        listeners: {
            onRawPreferences: "{that}.postHandler"
        }
    });

    gpii.preferencesServer.post.request = function (preferencesServer, request, ontologyHandler, event) {
        fluid.log("gpii.preferencesServer.post.request: entering");
        fluid.log("gpii.preferencesServer.post.request preferencesServer: " + JSON.stringify(preferencesServer, null, 2));
        fluid.log("gpii.preferencesServer.post.request.req.body: " + JSON.stringify(request.req.body, null, 2));
        //fluid.log("gpii.preferencesServer.post.request ontologyHandler: " + JSON.stringify(ontologyHandler, null, 2));

        // key preferences set by ontology
        fluid.log("gpii.preferencesServer.post.request: getting request view");
        var toView = gpii.preferencesServer.getRequestView(request);
        var payload = {};
        payload[toView] = request.req.body;

        fluid.log("gpii.preferencesServer.post.request: calling preferencesServer.setRawPreferences with payload: " + JSON.stringify(payload, null, 2));
        preferencesServer.setRawPreferences(event, payload, undefined);
    };

    gpii.preferencesServer.post.postHandler = function (reply, request, requestProxy) {
        fluid.log("gpii.preferencesServer.post.postHandler: entering");
        fluid.log("gpii.preferencesServer.post.postHandler reply: " + JSON.stringify(reply, null, 2));
        var ret = {
            userToken: reply.userToken,
            preferences: reply.preferences[request.req.query.view ? request.req.query.view : "flat"]
        };
        requestProxy.events.onSuccess.fire(ret);
    };
})();